import React, { useState } from 'react';
import './Storybook.css';

const Storybook = ({ story, onNewStory }) => {
  const [currentPage, setCurrentPage] = useState(0);

  // This is a placeholder - we'll need to adapt this based on the actual API response structure
  const pages = story.output?.pages || [];

  const nextPage = () => {
    if (currentPage < pages.length - 1) {
      setCurrentPage(currentPage + 1);
    }
  };

  const prevPage = () => {
    if (currentPage > 0) {
      setCurrentPage(currentPage - 1);
    }
  };

  if (!pages.length) {
    return (
      <div className="bg-red-50 text-red-600 p-6 rounded-xl max-w-2xl mx-auto">
        <h2 className="text-xl font-bold mb-2">Oops! Something went wrong</h2>
        <p>The API didn't return a proper story format.</p>
        <button 
          onClick={onNewStory}
          className="mt-4 bg-red-600 text-white px-4 py-2 rounded-lg"
        >
          Try Again
        </button>
      </div>
    );
  }

  return (
    <div className="max-w-4xl mx-auto">
      <h2 className="text-3xl font-bold text-center mb-8 text-purple-600">Your Personalized Story</h2>

      <div className="bg-white rounded-3xl shadow-xl overflow-hidden">
        <div className="p-6 md:p-10">
          {pages[currentPage].image && (
            <div className="mb-8 flex justify-center">
              <img 
                src={pages[currentPage].image} 
                alt={`Page ${currentPage + 1}`} 
                className="rounded-xl shadow-lg max-h-[400px]"
              />
            </div>
          )}

          <div className="prose max-w-none text-lg text-center md:text-xl">
            <p>{pages[currentPage].text}</p>
          </div>

          <div className="mt-8 text-center text-gray-500">
            Page {currentPage + 1} of {pages.length}
          </div>
        </div>

        <div className="bg-gray-50 p-6 flex flex-wrap justify-center gap-4">
          <button 
            onClick={prevPage} 
            disabled={currentPage === 0}
            className={`px-6 py-3 rounded-full flex items-center gap-2 ${
              currentPage === 0 
                ? 'bg-gray-200 text-gray-400 cursor-not-allowed' 
                : 'bg-blue-600 text-white hover:bg-blue-700'
            }`}
          >
            <i className="fa-solid fa-arrow-left"></i>
            Previous Page
          </button>

          <button 
            onClick={nextPage} 
            disabled={currentPage === pages.length - 1}
            className={`px-6 py-3 rounded-full flex items-center gap-2 ${
              currentPage === pages.length - 1 
                ? 'bg-gray-200 text-gray-400 cursor-not-allowed' 
                : 'bg-blue-600 text-white hover:bg-blue-700'
            }`}
          >
            Next Page
            <i className="fa-solid fa-arrow-right"></i>
          </button>

          <button 
            onClick={onNewStory} 
            className="px-6 py-3 rounded-full bg-purple-600 text-white hover:bg-purple-700 flex items-center gap-2"
          >
            <i className="fa-solid fa-plus"></i>
            Create New Story
          </button>
        </div>
      </div>
    </div>
  );
};

export default Storybook;
