import React, { useState } from 'react';
import './App.css';
import StoryForm from './components/StoryForm';
import Storybook from './components/Storybook';
import { parseStoryResponse } from './utils/storyParser';

function App() {
  const [story, setStory] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [streamingData, setStreamingData] = useState([]);
  const [isStreaming, setIsStreaming] = useState(false);
  const [storyHistory, setStoryHistory] = useState('');
  const [storyOptions, setStoryOptions] = useState([]);

  const generateStory = async (formData, selectedOption = null) => {
    setLoading(true);
    setError(null);
    setStreamingData([]);
    setIsStreaming(true);

    // If continuing the story with an option, update the history
    let history = storyHistory;
    if (selectedOption) {
      history = history + ' ' + selectedOption;
    } else {
      // Reset history if starting a new story
      history = ' ';
    }

    try {
      const response = await fetch('/api/released-app/5f0bbd5b-d933-4fac-912f-aaa4e6772386/run', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': 'Bearer ww-EFkpTZJlBulVgy6lkkX4KY01Q6UtN6g9x0dC3MdGkwVHIpn0jjVbOt'
        },
        body: JSON.stringify({
          inputs: {
            childname: formData.childName,
            interests: formData.interests,
            readinglevel: formData.readingLevel,
            history: history
          },
          version: "^1.0"
        })
      });

      if (!response.ok) {
        throw new Error('Failed to generate story');
      }


      // For streaming, just display the raw text first
      const text = await response.text();
      console.log(text);

      // Try to extract and display any images from the response
      const imageUrlRegex = /"image_url":"([^"]+)"/g;
      let match;
      const imageUrls = [];

      while ((match = imageUrlRegex.exec(text)) !== null) {
        imageUrls.push(match[1]);
      }

      // Extract text content
      const textContentRegex = /"(introduction|adventure|resolution|continuation)_text":"([^"]+)"/g;
      const textContents = [];

      while ((match = textContentRegex.exec(text)) !== null) {
        textContents.push({
          type: match[1],
          text: match[2]
        });
      }

      // Extract options from the last page
      const optionsRegex = /"options":\s*\[(.*?)\]/;
      const optionsMatch = text.match(optionsRegex);
      let options = [];

      if (optionsMatch && optionsMatch[1]) {
        try {
          // Try to parse the options array
          const optionsText = `[${optionsMatch[1]}]`;
          // Replace escaped quotes and handle potential JSON issues
          const cleanedText = optionsText.replace(/\\"/g, '"').replace(/\\\\/g, '\\');
          options = JSON.parse(cleanedText);
          console.log('Successfully parsed options:', options);
        } catch (e) {
          console.error('Failed to parse options:', e);
          // Fallback: try to extract options with regex
          const optionItemsRegex = /"([^"]+)"/g;
          let optionMatch;
          while ((optionMatch = optionItemsRegex.exec(optionsMatch[1])) !== null) {
            options.push(optionMatch[1]);
          }
          console.log('Extracted options with regex fallback:', options);
        }
      }

      // Update story history with the current story text
      const allText = textContents.map(content => content.text).join(' ');
      setStoryHistory(history ? history + ' ' + allText : allText);
      setStoryOptions(options);

      // Add this after extracting options
      console.log('Extracted options:', options);
      console.log('Extracted pages:', textContents);

      // Create a simple story object from the extracted data
      if (imageUrls.length > 0 && textContents.length > 0) {
        // Create pages from the text contents, ensuring each page has the correct text
        const pages = [];

        // Add each text content as a separate page with its corresponding image
        textContents.forEach((content, index) => {
          pages.push({
            text: content.text,
            image: index < imageUrls.length ? imageUrls[index] : null,
            type: content.type
          });
        });

        // Store the child's name for continuation
        setStory({
          output: { 
            pages,
            options
          },
          childName: formData.childName,
          interests: formData.interests,
          readingLevel: formData.readingLevel
        });
      } else {
        // Try to parse as JSON as a fallback
        try {
          const data = JSON.parse(text);
          const parsedStory = parseStoryResponse(data);
          setStory(parsedStory);
        } catch (e) {
          console.error('Failed to parse response:', e);
          setError('Failed to parse the story response');
        }
      }

      // Display the raw response for debugging
      setStreamingData([{ type: 'raw', value: text }]);

    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
      setIsStreaming(false);
    }
  };

  const continueStory = (option) => {
    console.log('Selected option:', option);
    console.log('Current story history:', storyHistory);

    // Use the last form data to continue the story
    const formData = {
      childName: story.childName || '',
      interests: story.interests || 'Futbol',
      readingLevel: story.readingLevel || 'Level 1'
    };

    generateStory(formData, option);
  };

  return (
    <div className="App">
      <header className="App-header-minimal">
        <div className="logo-container">
          <i className="fa-solid fa-book-open"></i>
          <span className="logo-text">StoryCraft</span>
        </div>
      </header>
      <main>
        {!story && !isStreaming && <StoryForm onSubmit={generateStory} />}
        {loading && <div className="loading">Generating your storybook... Please wait!</div>}
        {error && <div className="error">Error: {error}</div>}

        {isStreaming && streamingData.length > 0 && (
          <div className="streaming-container">
            <h2>Generating your story...</h2>
            <div className="streaming-content">
              {streamingData.map((chunk, index) => {
                // Display text chunks
                if (chunk.type === 'chunk' && chunk.value.type === 'chunk') {
                  return <span key={index}>{chunk.value.value}</span>;
                }

                // Display images as they come in
                if (chunk.type === 'chunk' && chunk.value.type === 'tool' && 
                    chunk.value.output && chunk.value.output.type === 'image') {
                  return (
                    <div key={index} className="streaming-image">
                      <img src={chunk.value.output.image_url} alt="Story illustration" />
                    </div>
                  );
                }

                return null;
              })}
            </div>
          </div>
        )}

        {story && !loading && (
          <Storybook 
            story={story} 
            options={storyOptions}
            onSelectOption={continueStory}
            onNewStory={() => {
              setStory(null);
              setStreamingData([]);
              setStoryHistory('');
              setStoryOptions([]);
            }} 
          />
        )}
      </main>
      <footer>
        <p>Powered by Wordware AI</p>
      </footer>
    </div>
  );
}

export default App;
