import React, { useState } from 'react';
import './App.css';
import StoryForm from './components/StoryForm';
import Storybook from './components/Storybook';
import { parseStoryResponse } from './utils/storyParser';

function App() {
  const [story, setStory] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [streamingData, setStreamingData] = useState([]);
  const [isStreaming, setIsStreaming] = useState(false);
  const [showStoryForm, setShowStoryForm] = useState(false);

  const generateStory = async (formData) => {
    setLoading(true);
    setError(null);
    setStreamingData([]);
    setIsStreaming(true);

    try {
      const response = await fetch('/api/released-app/03ebb9ea-1fda-493c-86da-597e2801635e/run', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': 'Bearer ww-5916h52zF1qi3SxMASUJ1HScxn1LibbpYmewHyfrJBs56tixAjGvR1'
        },
        body: JSON.stringify({
          inputs: {
            childname: formData.childName,
            interests: formData.interests,
            readinglevel: formData.readingLevel
          },
          version: "^3.0"
        })
      });

      if (!response.ok) {
        throw new Error('Failed to generate story');
      }

      // For streaming, just display the raw text first
      const text = await response.text();

      // Try to extract and display any images from the response
      const imageUrlRegex = /"image_url":"([^"]+)"/g;
      let match;
      const imageUrls = [];

      while ((match = imageUrlRegex.exec(text)) !== null) {
        imageUrls.push(match[1]);
      }

      // Extract text content
      const textContentRegex = /"(introduction|adventure|resolution)_text":"([^"]+)"/g;
      const textContents = [];

      while ((match = textContentRegex.exec(text)) !== null) {
        textContents.push({
          type: match[1],
          text: match[2]
        });
      }

      // Create a simple story object from the extracted data
      if (imageUrls.length > 0 && textContents.length > 0) {
        const pages = textContents.map((content, index) => ({
          text: content.text,
          image: index < imageUrls.length ? imageUrls[index] : null
        }));

        setStory({
          output: { pages }
        });
      } else {
        // Try to parse as JSON as a fallback
        try {
          const data = JSON.parse(text);
          const parsedStory = parseStoryResponse(data);
          setStory(parsedStory);
        } catch (e) {
          console.error('Failed to parse response:', e);
          setError('Failed to parse the story response');
        }
      }

      // Display the raw response for debugging
      setStreamingData([{ type: 'raw', value: text }]);

    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
      setIsStreaming(false);
    }
  };

  // Function to parse streaming response data
  const parseStreamingResponse = (data) => {
    if (data.type === 'outputs') {
      const storyData = data.values;

      // Extract text and images
      const pages = [];

      if (storyData.story_generation) {
        // Add introduction page
        if (storyData.story_generation.introduction_text) {
          pages.push({
            text: storyData.story_generation.introduction_text,
            image: storyData.Intro_image?.output?.image_url || null
          });
        }

        // Add adventure page
        if (storyData.story_generation.adventure_text) {
          pages.push({
            text: storyData.story_generation.adventure_text,
            image: storyData.adventure_image?.output?.image_url || null
          });
        }

        // Add resolution page
        if (storyData.story_generation.resolution_text) {
          pages.push({
            text: storyData.story_generation.resolution_text,
            image: storyData.Resolution_image?.output?.image_url || null
          });
        }
      }

      return {
        output: {
          pages
        }
      };
    }

    return null;
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-blue-50 to-purple-50">
      {/* Header */}
      <header className="fixed w-full bg-white/90 backdrop-blur-sm shadow-sm z-50">
        <div className="container mx-auto px-4 py-3 flex justify-between items-center">
          <div className="flex items-center gap-2">
            <i className="fa-solid fa-book-open text-purple-600 text-2xl"></i>
            <span className="text-xl font-bold bg-gradient-to-r from-purple-600 to-blue-600 bg-clip-text text-transparent">StoryCraft</span>
          </div>
          <button 
            className="bg-purple-600 text-white px-4 py-2 rounded-full text-sm"
            onClick={() => setShowStoryForm(true)}
          >
            Get Started
          </button>
        </div>
      </header>

      {/* Main Content */}
      <main className="pt-24">
        {/* Hero Section (only show if not creating a story) */}
        {!showStoryForm && !story && !isStreaming && (
          <section className="h-[700px]">
            <div className="container mx-auto px-4">
              <div className="flex flex-col md:flex-row items-center gap-8">
                <div className="flex-1 space-y-6">
                  <h1 className="text-4xl md:text-5xl font-bold text-gray-800">
                    AI-Powered Stories<br/>
                    <span className="text-purple-600">Just for Your Child</span>
                  </h1>
                  <p className="text-gray-600 text-lg">
                    Create personalized reading adventures that grow with your child's abilities and interests.
                  </p>
                  <div className="flex gap-4">
                    <button 
                      className="bg-purple-600 text-white px-6 py-3 rounded-full text-lg flex items-center gap-2"
                      onClick={() => setShowStoryForm(true)}
                    >
                      <i className="fa-solid fa-magic-wand-sparkles"></i>
                      Create Story
                    </button>
                    <button className="border-2 border-purple-600 text-purple-600 px-6 py-3 rounded-full text-lg">
                      Learn More
                    </button>
                  </div>
                </div>
                <div className="flex-1">
                  <img className="w-full h-auto rounded-3xl shadow-2xl" src="https://storage.googleapis.com/uxpilot-auth.appspot.com/4796b7851d-08fcfd4502f1ae248dbd.png" alt="colorful illustration of a happy child reading a magical glowing book" />
                </div>
              </div>
            </div>
          </section>
        )}

        {/* Story Form Section */}
        {showStoryForm && !story && !isStreaming && (
          <section className="py-20 bg-white">
            <div className="container mx-auto px-4 max-w-2xl">
              <div className="bg-gradient-to-r from-purple-100 to-blue-100 p-8 rounded-3xl">
                <h2 className="text-3xl font-bold text-center mb-8">Let's Create Your Story</h2>
                <StoryForm onSubmit={generateStory} />
              </div>
            </div>
          </section>
        )}

        {/* Loading State */}
        {loading && (
          <div className="container mx-auto px-4 py-20 text-center">
            <div className="text-2xl text-purple-600 mb-4">
              <i className="fa-solid fa-wand-magic-sparkles text-3xl animate-pulse"></i>
            </div>
            <h2 className="text-2xl font-bold mb-4">Creating Your Magical Story...</h2>
            <p className="text-gray-600">Our AI is crafting a unique adventure just for you!</p>
          </div>
        )}

        {/* Error State */}
        {error && (
          <div className="container mx-auto px-4 py-20">
            <div className="bg-red-50 text-red-600 p-6 rounded-xl max-w-2xl mx-auto">
              <h2 className="text-xl font-bold mb-2">Oops! Something went wrong</h2>
              <p>{error}</p>
              <button 
                className="mt-4 bg-red-600 text-white px-4 py-2 rounded-lg"
                onClick={() => {
                  setError(null);
                  setShowStoryForm(true);
                }}
              >
                Try Again
              </button>
            </div>
          </div>
        )}

        {/* Streaming Data Display */}
        {isStreaming && streamingData.length > 0 && (
          <div className="container mx-auto px-4 py-10">
            <div className="bg-white p-6 rounded-xl shadow-lg max-w-4xl mx-auto">
              <h2 className="text-2xl font-bold mb-4 text-purple-600">
                <i className="fa-solid fa-wand-magic-sparkles mr-2"></i>
                Generating Your Story...
              </h2>
              <div className="streaming-content">
                {streamingData.map((chunk, index) => {
                  if (chunk.type === 'raw') {
                    // Display raw text with line breaks
                    return (
                      <pre key={index} className="raw-response text-xs text-gray-500 overflow-x-auto">
                        {chunk.value}
                      </pre>
                    );
                  }

                  return null;
                })}
              </div>
              <div className="mt-4 flex justify-center">
                <div className="animate-pulse flex space-x-2">
                  <div className="w-3 h-3 bg-purple-400 rounded-full"></div>
                  <div className="w-3 h-3 bg-purple-500 rounded-full"></div>
                  <div className="w-3 h-3 bg-purple-600 rounded-full"></div>
                </div>
              </div>
            </div>
          </div>
        )}

        {/* Story Display */}
        {story && !loading && (
          <div className="container mx-auto px-4 py-10">
            <Storybook 
              story={story} 
              onNewStory={() => {
                setStory(null);
                setStreamingData([]);
                setShowStoryForm(true);
              }} 
            />
          </div>
        )}
      </main>

      {/* Footer */}
      <footer className="bg-gray-900 text-white py-12">
        <div className="container mx-auto px-4">
          <div className="grid md:grid-cols-4 gap-8">
            <div className="space-y-4">
              <div className="flex items-center gap-2">
                <i className="fa-solid fa-book-open text-purple-400 text-2xl"></i>
                <span className="text-xl font-bold">StoryCraft</span>
              </div>
              <p className="text-gray-400">Making reading magical for every child.</p>
            </div>
            <div>
              <h4 className="font-bold mb-4">Product</h4>
              <ul className="space-y-2 text-gray-400">
                <li>Features</li>
                <li>Pricing</li>
                <li>How it Works</li>
              </ul>
            </div>
            <div>
              <h4 className="font-bold mb-4">Company</h4>
              <ul className="space-y-2 text-gray-400">
                <li>About Us</li>
                <li>Contact</li>
                <li>Privacy Policy</li>
              </ul>
            </div>
            <div>
              <h4 className="font-bold mb-4">Follow Us</h4>
              <div className="flex gap-4">
                <i className="fa-brands fa-twitter text-2xl text-gray-400 hover:text-white"></i>
                <i className="fa-brands fa-instagram text-2xl text-gray-400 hover:text-white"></i>
                <i className="fa-brands fa-facebook text-2xl text-gray-400 hover:text-white"></i>
              </div>
            </div>
          </div>
          <div className="border-t border-gray-800 mt-8 pt-8 text-center text-gray-400">
            <p>&copy; 2025 StoryCraft. All rights reserved.</p>
          </div>
        </div>
      </footer>
    </div>
  );
}

export default App;
