import React, { useState } from 'react';
import './Storybook.css';

const Storybook = ({ story, onNewStory }) => {
  const [currentPage, setCurrentPage] = useState(0);

  // Ensure we have valid pages
  const pages = story?.output?.pages || [];
  const totalPages = pages.length; // Allow for 4 pages now
  const isLastPage = currentPage === totalPages - 1;

  // Get options from the last page if available
  const options = isLastPage && pages[currentPage]?.options 
    ? pages[currentPage].options 
    : [];

  const goToNextPage = () => {
    if (currentPage < totalPages - 1) {
      setCurrentPage(currentPage + 1);
    }
  };

  const goToPrevPage = () => {
    if (currentPage > 0) {
      setCurrentPage(currentPage - 1);
    }
  };

  const handleOptionSelect = (option) => {
    // Create a new story with the selected option as history
    onNewStory({
      childName: option.childName || "",
      interests: option.interests || "",
      readingLevel: option.readingLevel || "Level 1",
      history: story.output.rawResponse // Pass the current story as history
    });
  };

  // If no pages, show an error
  if (totalPages === 0) {
    return (
      <div className="storybook-container">
        <div className="storybook-error">
          <h3>Oops! Something went wrong.</h3>
          <p>We couldn't generate a story. Please try again.</p>
          <button onClick={() => onNewStory({})} className="new-story-btn">Try Again</button>
        </div>
      </div>
    );
  }

  return (
    <div className="storybook-container">
      <div className="storybook">
        <div className="page-counter">
          Page {currentPage + 1} of {totalPages}
        </div>

        <div className="page-content">
          {pages[currentPage]?.image && (
            <div className="page-image">
              <img src={pages[currentPage].image} alt={`Story illustration page ${currentPage + 1}`} />
            </div>
          )}

          <div className="page-text">
            {pages[currentPage]?.text || "No text available for this page."}
          </div>

          {/* Add the choice buttons on the last page */}
          {isLastPage && options.length > 0 && (
            <div className="story-choices">
              <h3>What would you like to do?</h3>
              <div className="choice-buttons">
                {options.map((option, index) => (
                  <button 
                    key={index}
                    className={`choice-btn ${index === 0 ? 'keep-btn' : 'new-btn'}`}
                    onClick={() => handleOptionSelect(option)}
                  >
                    <i className={`fa-solid ${index === 0 ? 'fa-heart' : 'fa-wand-magic-sparkles'}`}></i> 
                    {option.text || `Option ${index + 1}`}
                  </button>
                ))}
              </div>
            </div>
          )}

          {/* Fallback if no options are provided */}
          {isLastPage && options.length === 0 && (
            <div className="story-choices">
              <h3>What would you like to do?</h3>
              <div className="choice-buttons">
                <button className="choice-btn keep-btn">
                  <i className="fa-solid fa-heart"></i> Keep Dragon
                </button>
                <button className="choice-btn new-btn" onClick={() => onNewStory({})}>
                  <i className="fa-solid fa-wand-magic-sparkles"></i> Get a New Dragon
                </button>
              </div>
            </div>
          )}
        </div>

        <div className="page-navigation">
          <button 
            onClick={goToPrevPage} 
            disabled={currentPage === 0}
            className="nav-btn"
          >
            ← Previous
          </button>

          <button 
            onClick={goToNextPage} 
            disabled={currentPage === totalPages - 1}
            className="nav-btn"
          >
            Next →
          </button>
        </div>
      </div>

      {!isLastPage && (
        <button onClick={() => onNewStory({})} className="new-story-btn">
          Create New Story
        </button>
      )}
    </div>
  );
};

export default Storybook;
