import React, { useState } from 'react';
import './StoryForm.css';

const StoryForm = ({ onSubmit }) => {
  const [formData, setFormData] = useState({
    childName: '',
    interests: 'Futbol',
    readingLevel: 'Level 1'
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevData => ({
      ...prevData,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(formData);
  };

  const setInterest = (interest) => {
    setFormData(prevData => ({
      ...prevData,
      interests: interest
    }));
  };

  const setReadingLevel = (level) => {
    setFormData(prevData => ({
      ...prevData,
      readingLevel: level
    }));
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-6">
      <div className="space-y-4">
        <input
          type="text"
          name="childName"
          value={formData.childName}
          onChange={handleChange}
          placeholder="Child's Name"
          className="w-full px-4 py-3 rounded-xl border border-gray-200 focus:border-purple-400 focus:ring-2 focus:ring-purple-200 outline-none"
          required
        />

        <div className="space-y-2">
          <label className="font-semibold">Select Reading Level</label>
          <div className="flex gap-3">
            <button 
              type="button" 
              className={`flex-1 px-4 py-3 rounded-xl ${formData.readingLevel === 'Level 1' ? 'bg-purple-600 text-white' : 'bg-purple-100 text-purple-600'} hover:bg-purple-200 font-semibold`}
              onClick={() => setReadingLevel('Level 1')}
            >
              Level 1
            </button>
            <button 
              type="button" 
              className={`flex-1 px-4 py-3 rounded-xl ${formData.readingLevel === 'Level 2' ? 'bg-purple-600 text-white' : 'bg-purple-100 text-purple-600'} hover:bg-purple-200 font-semibold`}
              onClick={() => setReadingLevel('Level 2')}
            >
              Level 2
            </button>
            <button 
              type="button" 
              className={`flex-1 px-4 py-3 rounded-xl ${formData.readingLevel === 'Level 3' ? 'bg-purple-600 text-white' : 'bg-purple-100 text-purple-600'} hover:bg-purple-200 font-semibold`}
              onClick={() => setReadingLevel('Level 3')}
            >
              Level 3
            </button>
          </div>
        </div>
      </div>

      <div className="space-y-4">
        <h3 className="text-lg font-semibold">Select Interests</h3>
        <div className="flex flex-wrap gap-3">
          <button 
            type="button" 
            className={`px-4 py-2 rounded-full ${formData.interests === 'Futbol' ? 'bg-purple-600 text-white' : 'bg-purple-100 text-purple-600'} hover:bg-purple-200`}
            onClick={() => setInterest('Futbol')}
          >
            <i className="fa-solid fa-futbol mr-2"></i>Futbol
          </button>
          <button 
            type="button" 
            className={`px-4 py-2 rounded-full ${formData.interests === 'Space' ? 'bg-purple-600 text-white' : 'bg-purple-100 text-purple-600'} hover:bg-purple-200`}
            onClick={() => setInterest('Space')}
          >
            <i className="fa-solid fa-rocket mr-2"></i>Space
          </button>
          <button 
            type="button" 
            className={`px-4 py-2 rounded-full ${formData.interests === 'Minecraft' ? 'bg-purple-600 text-white' : 'bg-purple-100 text-purple-600'} hover:bg-purple-200`}
            onClick={() => setInterest('Minecraft')}
          >
            <i className="fa-solid fa-cube mr-2"></i>Minecraft
          </button>
          <button 
            type="button" 
            className={`px-4 py-2 rounded-full ${formData.interests === 'Dragons' ? 'bg-purple-600 text-white' : 'bg-purple-100 text-purple-600'} hover:bg-purple-200`}
            onClick={() => setInterest('Dragons')}
          >
            <i className="fa-solid fa-dragon mr-2"></i>Dragons
          </button>
        </div>
      </div>

      <button 
        type="submit" 
        className="w-full bg-purple-600 text-white py-4 rounded-xl text-lg font-semibold hover:bg-purple-700 transition-colors"
      >
        Generate My Story
      </button>
    </form>
  );
};

export default StoryForm;
