import React, { useState } from 'react';
import './App.css';
import StoryForm from './components/StoryForm';
import Storybook from './components/Storybook';
import { parseStoryResponse } from './utils/storyParser';

function App() {
  const [story, setStory] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const generateStory = async (formData) => {
    setLoading(true);
    setError(null);

    try {
      // Use a CORS proxy
      const corsProxy = 'https://cors-anywhere.herokuapp.com/';
      const apiUrl = 'https://app.wordware.ai/api/released-app/03ebb9ea-1fda-493c-86da-597e2801635e/run';

      const response = await fetch(corsProxy + apiUrl, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': 'Bearer ww-5916h52zF1qi3SxMASUJ1HScxn1LibbpYmewHyfrJBs56tixAjGvR1',
          'Origin': window.location.origin
        },
        body: JSON.stringify({
          inputs: {
            childname: formData.childName,
            interests: formData.interests,
            readinglevel: formData.readingLevel
          },
          version: "^3.0"
        })
      });

      if (!response.ok) {
        throw new Error('Failed to generate story');
      }

      const data = await response.json();
      const parsedStory = parseStoryResponse(data);
      setStory(parsedStory);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Personalized Storybook Generator</h1>
      </header>
      <main>
        {!story && <StoryForm onSubmit={generateStory} />}
        {loading && <div className="loading">Generating your storybook... Please wait!</div>}
        {error && <div className="error">Error: {error}</div>}
        {story && !loading && <Storybook story={story} onNewStory={() => setStory(null)} />}
      </main>
      <footer>
        <p>Powered by Wordware AI</p>
      </footer>
    </div>
  );
}

export default App;
